local function ad() if Config.Framework=='qbox' then return Aegis.Adapters.qbox end return Aegis.Adapters.qb end Aegis=Aegis or {}; Aegis.Capabilities=Aegis.Capabilities or {}; Aegis.Capabilities['economy.get']=function(s,d) return ad().GetMoney(tonumber(d.target)) end Aegis.Capabilities['economy.giveCash']=function(s,d) return ad().GiveCash(tonumber(d.target), tonumber(d.amount) or 0) end Aegis.Capabilities['economy.setBank']=function(s,d) return ad().SetBank(tonumber(d.target), tonumber(d.amount) or 0) end