local function ad() if Config.Framework=='qbox' then return Aegis.Adapters.qbox end return Aegis.Adapters.qb end Aegis=Aegis or {}; Aegis.Capabilities=Aegis.Capabilities or {}; Aegis.Capabilities['inventory.view']=function(s,d) return ad().GetInventory(tonumber(d.target)) end Aegis.Capabilities['inventory.give']=function(s,d) return ad().GiveItem(tonumber(d.target), d.item, tonumber(d.amount) or 1) end Aegis.Capabilities['inventory.take']=function(s,d) return ad().TakeItem(tonumber(d.target), d.item, tonumber(d.amount) or 1) end