local function ad() if Config.Framework=='qbox' then return Aegis.Adapters.qbox end return Aegis.Adapters.qb end Aegis=Aegis or {}; Aegis.Capabilities=Aegis.Capabilities or {}; Aegis.Capabilities['players.freeze']=function(s,d) TriggerClientEvent('aegis:client:freeze', tonumber(d.target), true) end Aegis.Capabilities['players.unfreeze']=function(s,d) TriggerClientEvent('aegis:client:freeze', tonumber(d.target), false) end Aegis.Capabilities['players.heal']=function(s,d) TriggerClientEvent('aegis:client:heal', tonumber(d.target)) end Aegis.Capabilities['players.revive']=function(s,d) TriggerClientEvent('aegis:client:revive', tonumber(d.target)) end Aegis.Capabilities['players.tp.bring']=function(s,d) local p=GetPlayerPed(s); local c=GetEntityCoords(p); TriggerClientEvent('aegis:client:tp', tonumber(d.target), c.x,c.y,c.z) end Aegis.Capabilities['players.tp.goto']=function(s,d) local p=GetPlayerPed(tonumber(d.target)); local c=GetEntityCoords(p); TriggerClientEvent('aegis:client:tp', s, c.x,c.y,c.z) end Aegis.Capabilities['players.list']=function(s,d) return ad().GetPlayers() end Aegis.Capabilities['players.spectate']=function(s,d) TriggerClientEvent('aegis:client:spectate', s, tonumber(d.target), false) end Aegis.Capabilities['players.spectate.follow']=function(s,d) TriggerClientEvent('aegis:client:spectate', s, tonumber(d.target), true) end